package com.sharehome.platform.model;

public class Admin {
		private Integer id; //  主键ID	private String loginName; //  登录名	private String name; //  姓名	private String password; //  登录密码	private Integer status; //  状态	public Integer getId() {	    return this.id;	}	public void setId(Integer id) {	    this.id=id;	}	public String getLoginName() {	    return this.loginName;	}	public void setLoginName(String loginName) {	    this.loginName=loginName;	}	public String getName() {	    return this.name;	}	public void setName(String name) {	    this.name=name;	}	public String getPassword() {	    return this.password;	}	public void setPassword(String password) {	    this.password=password;	}	public Integer getStatus() {	    return this.status;	}	public void setStatus(Integer status) {	    this.status=status;	}
	
}
